mapper.update:
UPDATE sys_user
SET status = #{status},
update_time = now(),
update_by = #{updateBy}
WHERE user_id in

    #{id}

UPDATE sys_user
SET del_flag = 2,
update_time = now(),
update_by=#{updateBy}
WHERE user_id in

    #{id}

UPDATE sys_user
SET password = #{password}
WHERE user_id in

    #{id}

mapper.select:
SELECT s.status
FROM sys_user s WHERE s.user_id IN

    #{id}

SELECT COUNT(user_id) count, user_id userId
FROM sys_user
WHERE user_name = #{userName}

SELECT COUNT(user_id) count, user_id userId
FROM sys_user
WHERE email = #{email}

SELECT COUNT(user_id) count, user_id userId
FROM sys_user
WHERE phonenumber = #{phoneNumber}
