mapper.update:
UPDATE t_order SET del_flag = 2,
update_time = now(),
update_by= #{updateBy}
WHERE id in

    #{id}

UPDATE t_order
SET status      = #{status},
    update_time = NOW(),
    update_by= #{updateBy}
WHERE id = #{id}

mapper.select:
SELECT t.id, t.user_id, t.status, s.nick_name, t.order_code, t.order_name, t.empty_box_order_time, t.heavy_box_order_time, t.empty_box_call_name, t.empty_box_call_phone, t.empty_box_call_address, t.empty_box_call_time, t.empty_box_call_interval, t.heavy_box_call_name, t.heavy_box_call_phone, t.heavy_box_call_address, t.heavy_box_call_time, t.heavy_box_call_interval, t.box_id, t.box_code, t.box_standard, t.box_unit_price, t.lease_duration, t.total_price, t.sort_no, t.create_time, t.create_by, t.update_time, t.update_by, t.version, t.del_flag, t.app_del_flag, t.backend_del_flag FROM t_order t LEFT JOIN sys_user s ON t.user_id = s.user_id
t.del_flag = 0
AND s.nick_name like concat('%', #{nickName},'%')
AND t.status =#{status}
AND t.empty_box_order_time BETWEEN #{emptyBoxOrderTimeStart} AND #{emptyBoxOrderTimeEnd}
AND t.heavy_box_order_time BETWEEN #{heavyBoxOrderTimeStart} AND #{heavyBoxOrderTimeEnd}

SELECT t.id, t.user_id, s.nick_name, t.status, t.order_code, t.order_name, t.empty_box_order_time, t.heavy_box_order_time, t.empty_box_call_name, t.empty_box_call_phone, t.empty_box_call_address, t.empty_box_call_time, t.empty_box_call_interval, t.heavy_box_call_name, t.heavy_box_call_phone, t.heavy_box_call_address, t.heavy_box_call_time, t.heavy_box_call_interval, t.box_id, t.box_code, t.box_standard, t.box_unit_price, t.lease_duration, t.total_price, t.sort_no, t.create_time, t.create_by, t.update_time, t.update_by, t.version, t.del_flag, t.app_del_flag, t.backend_del_flag FROM t_order t LEFT JOIN sys_user s ON t.user_id = s.user_id
t.del_flag = 0
AND t.id = #{id}
